#ifndef ZWTool_define_h
#define ZWTool_define_h


#import "UIView+ZW.h"



// 屏幕宽
#define WIDTH [UIScreen mainScreen].bounds.size.width
// 屏幕高
#define HEIGHT [UIScreen mainScreen].bounds.size.height

// block self 弱化 强化
#define kSelfWeak __weak typeof(self) weakSelf = self
#define kSelfStrong __strong __typeof__(self) strongSelf = weakSelf

// 屏幕 bounds
#define WindowBounds [UIScreen mainScreen].bounds

// 系统版本判断
#define ISIOS6 ([[[UIDevice currentDevice] systemVersion] floatValue] >= 6.0) // IOS6的系统
#define ISIOS7 ([[[UIDevice currentDevice] systemVersion] floatValue] >= 7.0) // IOS7的系统
#define ISIOS8 ([[[UIDevice currentDevice] systemVersion] floatValue] >= 8.0) // IOS8的系统
// 9.0
#define ISIOS9 ([[[UIDevice currentDevice] systemVersion] floatValue] >= 9.0)
#define KSystemVersionLess8 ([[[UIDevice currentDevice] systemVersion] floatValue] < 8.0)

//颜色色值 0x
#define UIColorFromRGB(rgbValue) [UIColor colorWithRed:((float)((rgbValue & 0xFF0000) >> 16))/255.0 green:((float)((rgbValue & 0xFF00) >> 8))/255.0 blue:((float)(rgbValue & 0xFF))/255.0 alpha:1.0]

// 设置颜色RGB
#define UIColorRGB(R,G,B) [UIColor colorWithRed:R/255.0 green:G/255.0 blue:B/255.0 alpha:1.0]

//字体大小
#define ZWfont(CGfloat) [UIFont systemFontOfSize:CGfloat]

// block self 弱化 强化
#define ZSelfWeak __weak typeof(self) weakSelf = self
#define ZSelfStrong __strong __typeof__(self) strongSelf = weakSelf

//UserDefault
#define ZUserDefault [NSUserDefaults standardUserDefaults]

//后台返给的唯一标识
#define UniqueID [ZUserDefault objectForKey:@"uuid"]

#define ZApplicationDelegate [UIApplication sharedApplication]
#define sankey @""


#define mo @""

//打印输出
#ifdef DEBUG
#define NSLog(FORMAT, ...) fprintf(stderr,"%s:%d  \t%s\n",[[[NSString stringWithUTF8String:__FILE__] lastPathComponent] UTF8String], __LINE__, [[NSString stringWithFormat:FORMAT, ##__VA_ARGS__] UTF8String]);
#else
#define NSLog(FORMAT, ...) nil
#endif

#endif /* ZWTool_define_h */
